import React from 'react';
import Grid from '@material-ui/core/Grid';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import axios from "axios";
import {CardHeader} from "@material-ui/core";
import IconButton from '@material-ui/core/IconButton';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import Collapse from '@material-ui/core/Collapse';
import { makeStyles } from '@material-ui/core/styles';

const media = {
    width: "200px",
    height: "300px",
    float: "left",
    padding: 10,
    display: "block",
    marginLeft: "auto",
    marginRight: "auto"
}

const card = {
    width: 1000,
    backgroundColor: "#CBCACA"
}

const expand = {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
};

const expandOpen = {
    transform: "rotate(180deg)"
}

class NovelsGrid extends React.Component {
    constructor() {
        super();
        this.state = {
            spacing: 4,
            items: [],
            expanded: false,
            setExpanded: false,
        }
    }

    componentDidMount() {
        axios.get('http://127.0.0.1:8000/api/novels').then(response => {
            this.setState({items: response.data});
        });
    }

    handleExpandClick = () => {
        this.state.setExpaded(!this.state.expanded);
    };

    render() {
        return (
            <Grid container>
                <Grid item xs={12}>
                    <Grid container justify="center" spacing={this.state.spacing}>
                        {this.state.items.map(value => {
                            return (
                                <Grid key={value.id} item>
                                    <Card style={card} variant="outlined">
                                        <CardMedia
                                            style={media}
                                            component="img"
                                            image={value.image}
                                            title={value.name}
                                        />
                                        <CardContent>
                                            <Typography gutterBottom variant="h6">
                                                {value.name}
                                            </Typography>
                                            <Typography variant="body1" color="textSecondary" component="p">
                                                {value.author}
                                            </Typography>
                                            <Typography variant="body1" color="textSecondary" component="p">
                                                {value.chcount}
                                            </Typography>
                                        </CardContent>
                                        <CardActions>
                                            <IconButton
                                                onClick={this.state.handleExpandClick}
                                                aria-expanded={this.state.expanded}
                                                aria-label="show description"
                                                style={expand}
                                            >
                                                <ExpandMoreIcon/>
                                            </IconButton>
                                        </CardActions>
                                        <Collapse in={this.state.setExpanded} timeout="auto" unmountOnExit>
                                            <CardContent>
                                                <Typography variant="body1" color="textSecondary" component="p">
                                                    {value.desc}
                                                </Typography>
                                            </CardContent>
                                        </Collapse>
                                    </Card>
                                </Grid>
                            );
                        })}
                    </Grid>
                </Grid>
            </Grid>
        );
    }
}

export default NovelsGrid;
