import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import axios from "axios";

class NovelsGrid extends React.Component {
    constructor() {
        super();
        this.state = {
            classes: makeStyles(theme => ({
                root: {
                    flexGrow: 1,
                    maxWidth: 345
                },
                paper: {
                    height: 500,
                    width: 500,
                },
            })),
            spacing: 10,
            items: []
        }
    }
    componentDidMount() {
        axios.get('http://127.0.0.1:8000/api/novels').then(response => {
            this.setState({ items: response.data });
        });
    }
    render() {
        return (
            <Grid container className={this.state.classes.root} spacing={2}>
                <Grid item xs={12}>
                    <Grid container justify="center" spacing={this.state.spacing}>
                        {this.state.items.map(value => {
                            return (
/*                                <Grid key={value.id} item>
                                    <Paper className={this.state.classes.paper}>
                                        {value.name}
                                    </Paper>
                                </Grid>*/
                                <Card className={this.state.classes.root}>
                                    <CardActionArea>
                                        <CardMedia
                                            component="img"
                                            height="200"
                                            image={value.image}
                                            title={value.name}
                                        />
                                        <CardContent>
                                            <Typography gutterBottom variant="h5" component="h2">
                                                {value.name}
                                            </Typography>
                                            <Typography variant="body2" color="textSecondary" component="p">
                                                {value.desc}
                                            </Typography>
                                        </CardContent>
                                        <CardActions>
                                            <Button size="small" color="primary">
                                                More
                                            </Button>
                                        </CardActions>
                                    </CardActionArea>
                                </Card>
                            )
                        })}
                    </Grid>
                </Grid>
            </Grid>
        );
    }
}

export default NovelsGrid;
