import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import FormLabel from '@material-ui/core/FormLabel';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import RadioGroup from '@material-ui/core/RadioGroup';
import Radio from '@material-ui/core/Radio';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Card from "@material-ui/core/Card";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import CardActions from "@material-ui/core/CardActions";
import IconButton from "@material-ui/core/IconButton";
import ExpandMoreIcon from "@material-ui/core/SvgIcon/SvgIcon";
import Collapse from "@material-ui/core/Collapse";

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        height: 140,
        width: 100,
    },
    control: {
        padding: theme.spacing(2),
    },
}));

export default function SpacingGrid(props) {
    const [spacing, setSpacing] = React.useState(2);
    const classes = useStyles();

    const handleChange = event => {
        setSpacing(Number(event.target.value));
    };

    return (
        <Grid container className={classes.root} spacing={2}>
            <Grid item xs={12}>
                <Grid container justify="center" spacing={spacing}>
                        {props.novels.map(value => {
                            return (
                                <Grid key={value.id} item>
                                    <Card style={card} variant="outlined">
                                        <CardMedia
                                            style={media}
                                            component="img"
                                            image={value.image}
                                            title={value.name}
                                        />
                                        <CardContent>
                                            <Typography gutterBottom variant="h6">
                                                {value.name}
                                            </Typography>
                                            <Typography variant="body1" color="textSecondary" component="p">
                                                {value.author}
                                            </Typography>
                                            <Typography variant="body1" color="textSecondary" component="p">
                                                {value.chcount}
                                            </Typography>
                                        </CardContent>
                                        <CardActions>
                                            <IconButton
                                                onClick={this.handleExpandClick}
                                                aria-expanded={this.state.setExpanded}
                                                aria-label="show description"
                                                style={expand}
                                            >
                                                <ExpandMoreIcon/>
                                            </IconButton>
                                        </CardActions>
                                        <Collapse in={this.state.setExpanded} timeout="auto" unmountOnExit>
                                            <CardContent>
                                                <Typography variant="body1" color="textSecondary" component="p" style={{whiteSpace: "pre-line"}}>
                                                    {value.desc}
                                                </Typography>
                                            </CardContent>
                                        </Collapse>
                                    </Card>
                                </Grid>
                            );
                        })}
                </Grid>
            </Grid>
        </Grid>
    );
}
